mapper.select:
select id,shop_id as shopId, customer_id as customerId, shop_name as shopName, money, phone, state, image, CREATE_TIME as createTime,appraise from cu_consume
 WHERE 1=1

      AND CUSTOMER_ID = #{customerId} 


      AND state = #{state} 


      AND shop_id = #{shopId} 


      AND id = #{id} 

 order by create_time desc

      limit #{pageN},#{pageS}

select count(*) from cu_consume
WHERE 1=1

     AND CUSTOMER_ID = #{customerId} 


     AND state = #{state} 


     AND shop_id = #{shopId} 


     AND id = #{id}

mapper.update:
UPDATE cu_consume
SHOP_ID = #{shopId}, CUSTOMER_ID = #{customerId}, SHOP_NAME = #{shopName}, MONEY = #{money}, PHONE = #{phone}, STATE = #{state}, IMAGE = #{image}, mtoken = #{mtoken}, appraise = #{appraise}
WHERE id = #{id}
