mapper.select:
select ADVERT_ID as id, CUSTOMER_ID as customerId, ADVERT_NAME as advertName, ADVERT_CONTEXT as advertContext, ADVERT_IMAGE as advertImage, SHOP_LICENCE as shopLicence, SHOP_VIDEO as shopVideo, ADVERT_PHONE as advertPhone, ADVERT_ADDRESS as advertAddress, ADVERT_LONGITUDE as advertLongitude, ADVERT_LATITUDE as advertLatitude, APPLY_STATUS as applyStatus, APPLY_CONTEXT as applyContext, FLAG_DEL as flagDel, CREATE_TIME as createTime, ADVERT_SALE as advertSale, CITY, REGION, PERSON from mall_shop_advert where 1=1
and advert_id = #{id}
and customer_id = #{customerId}
and apply_status = #{applyStatus}
and FLAG_DEL = #{flagDel}
and city = #{city}
and CLASSIFY_ID = #{classifyId}
and advert_name like '%${advertName}%'
order by CREATE_TIME DESC
order by advert_sale desc
limit #{pageN},#{pageS}

select
 count(*)
from mall_shop_advert
where 1=1

    and advert_id = #{id}


    and customer_id = #{customerId}


    and apply_status = #{applyStatus}


    and FLAG_DEL = #{flagDel}
