)VALUES(
#{id}, #{countryId}, #{customerNumber}, #{customerName}, #{customerHead}, #{customerPhone}, #{customerAlipay}, #{customerWechat}, #{customerEmail}, #{realName}, #{customerCard}, #{bankCard}, #{bankName}, #{flagAuth}, #{flagTrade}, #{flagSpecial}, #{customerStatus}, #{customerFreezeContext}, #{registerTime}, #{versionNum}, #{flagDel}, #{createTime}, #{createUser}, #{modifyUser}, #{modifyTime},
